<?phpclass BankAction extends BaseAction{    public function index()    {        $bank = new Model("banks");        $list=$bank->where("isshow=1")->select();        $this->assign("list",$list);        $this->display();    }    public function edit()    {        $id=$this->_get("id");        if($id==null)        {            $this->display();        }        else        {            $bank = new Model("banks");            $data = $bank->where("id=$id")->find();            $this->data=$data;            $this->display();                    }    }    public function save()    {        $data=$this->_post();        $bank = new Model("banks");        if($data["id"]==null)        {            $bank->add($data);        }        else        {            $bank->save($data);        }        $this->success("修改成功");    }    public function delete()    {        $id=$this->_get("id");        $bank = new Model("banks");        $bank->where("Id=$id")->delete();        $this->success("删除成功");    }}